<style>
	.slider {
		width: 100%;
		height: var(--height);
		margin: auto;
		position: relative;
		display: grid;
		place-items: center;
		overflow: hidden;
	}

	.slider::before,
	.slider::after {
		content: '';
		position: absolute;
		width: 15%;
		height: 100%;
		z-index: 2;
	}

	.slider::before {
		left: 0;
		top: 0;
		background: linear-gradient(to right, white 0%, transparent 100%);
	}

	.slider::after {
		right: 0;
		top: 0;
		background: linear-gradient(to left, white 0%, transparent 100%);
	}

	.slide-track {
		display: flex;
		width: calc(var(--width) * var(--quantity) * 2);
		animation: scroll 20s linear infinite;
	}

	.slide {
		width: var(--width);
		height: var(--height);
		display: flex;
		align-items: center;
		padding: 15px;
		perspective: 100px;
	}

	img {
		width: 100%;
		transition: transform 1s;
	}

	img:hover {
		transform: translateZ(20px);
	}

	@keyframes scroll {
		0% {
			transform: translateX(0);
		}
		100% {
			transform: translateX(calc(var(--width) * var(--quantity) * -1));
		}
	}
</style>
